mapper.select:
select um.usercode, um.phone, um.email, um.name, um.username,
ud.img_url, ud.website, ud.introduce, ud.gender,
um.create_date, um.update_date from user_mst um left outer join user_dtl ud on(ud.usercode = um.usercode) where um.usercode = #{usercode};

mapper.update:
update
	user_dtl
set
	img_url = #{img_url}
where
	usercode = #{usercode};
